package com.mcndsj.lobby_Gui.guis;import org.bukkit.Material;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.inventory.InventoryClickEvent;import org.bukkit.inventory.ItemStack;import com.mcndsj.lobby_Gui.LobbyGui;import com.mcndsj.lobby_Gui.items.AbstractItem;public abstract class AbstractGui implements Listener {	private String title = null;	protected AbstractItem item = null;		public AbstractGui(String title){		this.title =title;		LobbyGui.getInstance().getServer().getPluginManager().registerEvents(this, LobbyGui.getInstance());	}	public AbstractGui(){		LobbyGui.getInstance().getServer().getPluginManager().registerEvents(this, LobbyGui.getInstance());	}		@EventHandler	public void onInventoryClick(InventoryClickEvent evt){		try{			if(evt.getClickedInventory() == null ||					evt.getClickedInventory().getTitle() == null ||					!evt.getClickedInventory().getTitle().equals(title)){				return;			}		}catch(NullPointerException e){			return;		}				if(evt.getCurrentItem() == null || evt.getCurrentItem().getType() == Material.AIR)			return;						callOnClick((Player)evt.getWhoClicked(), evt.getCurrentItem());	}		protected abstract void callOnClick(Player p,ItemStack item );		protected void setAbstractItem (AbstractItem item){		this.item = item;		this.title = item.getTitle();	}		protected void setTitle(String title){		this.title = title;	}		public AbstractItem getItem(){		return item;	}	}